// Parse VAPI tool-calls payload (body wrapper for n8n cloud)
const data = items[0].json.body || items[0].json;
const toolCall = data.message.toolCallList[0];
const toolCallId = toolCall.id;
let args = toolCall.function.arguments;
if (typeof args === 'string') {
  args = JSON.parse(args);
}

return [{
  json: {
    toolCallId,
    call_id: args.call_id,
    field_name: args.field_name,
    field_value: args.field_value
  }
}];

const { field_name, field_value } = items[0].json;
let valid = true;
let error = '';

// Whitelist validation - only accept known field names
const VALID_FIELDS = [
  'caller_name', 'caller_phone', 'caller_email', 'caller_address',
  'caller_dob', 'caller_occupation', 'policy_type', 'referral_source',
  'claims_count', 'claims_details', 'urgency_timeline', 'current_coverage',
  'shopping_reason', 'auto_vehicle_info', 'auto_vin', 'auto_lien_type',
  'auto_lienholder', 'auto_violations', 'auto_household_drivers',
  'auto_title_names', 'renter_address', 'renter_possessions_value',
  'renter_mgmt_company', 'renter_addl_insured', 'property_address',
  'property_first_buyer', 'property_value', 'property_coverage_needs',
  'property_policy_age', 'biz_name', 'biz_ein', 'biz_start_date',
  'biz_revenue', 'biz_industry', 'biz_contact', 'cross_sell_interest',
  'cross_sell_types', 'reason_for_calling'
];
if (!VALID_FIELDS.includes(field_name)) {
  return [{ json: { ...items[0].json, valid: false, error: 'Unknown field: ' + field_name } }];
}

switch (field_name) {
  case 'caller_phone':
    valid = /^\+?1?\d{10}$/.test(field_value.replace(/[\s\-()]/g, ''));
    error = valid ? '' : 'Invalid phone number format';
    break;
  case 'caller_email':
    valid = /^[^@]+@[^@]+\.[^@]+$/.test(field_value);
    error = valid ? '' : 'Invalid email format';
    break;
  case 'caller_dob':
    const d = new Date(field_value);
    valid = !isNaN(d.getTime()) && d < new Date();
    error = valid ? '' : 'Invalid date of birth';
    break;
  default:
    valid = field_value && field_value.trim().length > 0;
    error = valid ? '' : 'Field cannot be empty';
}

return [{ json: { ...items[0].json, valid, error } }];

// Accumulate all fields for this call using workflow static data
const staticData = $getWorkflowStaticData('global');
const { call_id, field_name, field_value } = items[0].json;

// Initialize call data accumulator if first field
if (!staticData[call_id]) {
  staticData[call_id] = {};
}

// Store the new field
staticData[call_id][field_name] = field_value;

// Build a COMPLETE row with ALL fields collected so far
const row = {
  call_id: call_id,
  migration_flag: 'VAPI_AI_COLLECTED',
  updated_at: new Date().toISOString()
};

// Merge all accumulated fields into the row
for (const [key, value] of Object.entries(staticData[call_id])) {
  row[key] = value;
}

return [{ json: row }];

// Parse VAPI tool-calls payload (body wrapper for n8n cloud)
const data = items[0].json.body || items[0].json;
const toolCall = data.message.toolCallList[0];
const toolCallId = toolCall.id;
let p = toolCall.function.arguments;
if (typeof p === 'string') {
  p = JSON.parse(p);
}

const claimsThreshold = 3;
const urgencyThreshold = 72;

let disqualified = false;
let reason = '';

if (p.claims_count_worst_year >= claimsThreshold) {
  disqualified = true;
  reason = 'Too many claims: ' + p.claims_count_worst_year + ' in a single year';
} else if (p.urgency_hours <= urgencyThreshold && p.urgency_hours > 0) {
  disqualified = true;
  reason = 'Coverage needed within ' + p.urgency_hours + ' hours';
}

const result_message = disqualified
  ? 'DISQUALIFIED: ' + reason + '. Deliver the polite redirect message.'
  : 'Not disqualified. Continue with data collection.';

return [{ json: { toolCallId, call_id: p.call_id, disqualified, reason, result_message } }];

// Parse VAPI tool-calls payload (body wrapper for n8n cloud)
const data = items[0].json.body || items[0].json;
const toolCall = data.message.toolCallList[0];
const toolCallId = toolCall.id;
let p = toolCall.function.arguments;
if (typeof p === 'string') {
  p = JSON.parse(p);
}

let hot_lead = false;
let reason = '';

if (p.policy_type === 'property' && p.estimated_value >= 2000000) {
  hot_lead = true;
  reason = 'Property value $' + p.estimated_value.toLocaleString();
} else if (p.policy_type === 'auto' && p.estimated_value >= 180000) {
  hot_lead = true;
  reason = 'Auto value $' + p.estimated_value.toLocaleString();
}

const result_message = hot_lead
  ? 'HOT LEAD: ' + reason + '. Transfer caller to live agent immediately using transferCall.'
  : 'Not a hot lead. Continue with standard data collection.';

return [{ json: { toolCallId, call_id: p.call_id, hot_lead, reason, policy_type: p.policy_type, estimated_value: p.estimated_value, result_message } }];

