// Parse VAPI tool-calls payload (body wrapper for n8n cloud)
const data = items[0].json.body || items[0].json;
const toolCall = data.message.toolCallList[0];
const toolCallId = toolCall.id;
let args = toolCall.function.arguments;
if (typeof args === 'string') {
  args = JSON.parse(args);
}

return [{
  json: {
    toolCallId,
    ticket_id: (args.call_id || '') + '_existing',
    call_id: args.call_id,
    caller_name: args.caller_name,
    caller_phone: args.caller_phone,
    policy_type: args.policy_type || '',
    request_description: args.request_description,
    created_at: new Date().toISOString(),
    status: 'pending',
    escalated: 'false',
    assigned_to: 'Val',
    acknowledged_at: '',
    completed_at: ''
  }
}];

